// Declarations for SVG modules
declare module '*.svg' {
    const content: string;
    export default content;
  }
  
  // You can also add other custom type declarations here, for example:
  
  // Example: Declarations for importing other assets like images
  declare module '*.png' {
    const content: string;
    export default content;
  }
  
  declare module '*.jpg' {
    const content: string;
    export default content;
  }
  
  declare module '*.jpeg' {
    const content: string;
    export default content;
  }
  
  declare module '*.gif' {
    const content: string;
    export default content;
  }
  